contract UrbeCampusExample {

    //USDC has 6 decimals 
    // i want to transfer 10 usdc
    // 10 * 10^6 = 10000000

    //1 -> 1000000
    //0.1 -> 100000

    //EVENTS
    event NewDonation(address donor, uint256 amount, uint256 totalDonations);
    event Withdrawal(address donor, uint256 amount, uint256 totalDonations);
    event CreatorWithdraw(uint256 amount);

    //STORAGE VARIABLES
    uint256 public startTime;
    uint256 public endTime;
    uint256 public moneyRaised; //not initialized in constructor
    uint256 public moneyGoal;
    address public creator;
    address public tokenAddress;
    mapping(address => uint256) public donors;

    constructor(address _creator, uint256 _startTime, uint256 _endTime, uint256 _moneyGoal, address _tokenAddress) {
        require(_creator != address(0), "Creator cannot be the zero address");
        require(_startTime > block.timestamp, "Start time must be in the future");
        require(_endTime > _startTime, "End time must be after start time");
        require(_moneyGoal > 0, "Money goal must be greater than 0");
        require(_tokenAddress != address(0), "Token address cannot be the zero address");
        creator = _creator;
        tokenAddress = _tokenAddress;
        startTime = _startTime;
        endTime = _endTime;
        moneyGoal = _moneyGoal;
    }

    function donate(uint256 amount) public {
        validateDonation(msg.sender, amount);

        moneyRaised += amount; //-> moneyRaised = moneyRaised + amount
        donors[msg.sender] += amount; //-> donors[msg.sender] = donors[msg.sender] + amount

        require(IERC20(tokenAddress).transferFrom(msg.sender, address(this), amount), "Transfer failed");
        emit NewDonation(msg.sender, amount, moneyRaised);
    }

    function withdrawFunds() public {
        require(block.timestamp < endTime, "Campaign ended");
        require(donors[msg.sender] > 0, "No funds to withdraw");

        uint256 amountToWithdraw = donors[msg.sender];

        moneyRaised -= amountToWithdraw; //-> moneyRaised = moneyRaised - amountToWithdraw
        donors[msg.sender] = 0; 

        require(IERC20(tokenAddress).transfer(msg.sender, amountToWithdraw), "Transfer failed");
        emit Withdrawal(msg.sender, amountToWithdraw, moneyRaised);
    }

    function creatorWithdraw()public {
        require(msg.sender == creator, "Only creator can withdraw");
        require(block.timestamp >= endTime || moneyRaised == moneyGoal, "Campaign not ended or goal not reached");

        require(IERC20(tokenAddress).transfer(creator, moneyRaised), "Transfer failed");
        emit CreatorWithdraw(moneyRaised);
    } 


    function validateDonation(address _donor, uint256 _amount) internal {
        require(block.timestamp >= startTime, "Campaign not started");
        require(block.timestamp < endTime, "Campaign ended");
        require(_amount > 0, "Amount must be greater than 0");
        require(moneyRaised + _amount <= moneyGoal, "Campaign goal reached");

    }





    //uint8 2^8 = 256
